import React from 'react';
import { useEffect, useState } from 'react';
import {nanoid} from 'nanoid'
import Dice from './Dice'
import './App.css';


function App() {
  const [dice, setDice] = useState(resetDice())


  function generateDice() {
    return {
      id: nanoid(),
      value: Math.ceil(Math.random() * 6),
      isHeld: false,

    }
  }

  function resetDice() {
    const die = []

    for (let i = 0; i < 10; i++) {
      die.push(generateDice())
    }

    return die
  }

  const diceElements = dice.map(die => <Dice
    value={die.value}
    isHeld={die.isHeld}
  />)

  function handelDice() {
    dice.map(oldDice => oldDice.map(die => {
      return die.isHeld ? die : {...die, isHeld: die.isHeld}
    }))
  }

  function handelRoll() {
    setDice(resetDice())
  }

  return (
    <div className="dice">
      <div className='dice__elements'>
        {diceElements}
      </div>
      <button onClick={handelRoll}>Roll</button>
    </div>
  );
}

export default App;
